import React from 'react';
import { BsArrowRight } from "react-icons/bs";
import imagebanner from '../images/main-slide.jpg';
import Slider from 'infinite-react-carousel';



const SimpleSlider = () => {
    const settings = {
        dots: true,
        slidesToShow: 1,
        slidesToScroll: 1
    };

    return (
        <>
            <Carousel
                {...settings}
                ref={(ele) => { window.SlimpleSlider = ele; }}
            >
                <div>
                    <a href="javascript:void(0)"><img src={imagebanner} alt="Banner Image 1" /></a>
                </div>
                <div>
                    <a href="javascript:void(0)"><img src={imagebanner} alt="Banner Image 1" /></a>
                </div>
                <div>
                    <a href="javascript:void(0)"><img src={imagebanner} alt="Banner Image 1" /></a>
                </div>
            </Carousel>
        </>
        );
};


export default SimpleSlider;
